import React, { useEffect, useRef, useState } from 'react';
import { View, Text, ScrollView, FlatList, Image, Dimensions, ActivityIndicator, Pressable, Modal, StyleSheet } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import { TouchableOpacity } from 'react-native';
import { StatusBar } from 'expo-status-bar';
import axios from 'axios';
import { useSelector } from 'react-redux';
import { LinearGradient } from 'expo-linear-gradient';
import CustomButton from '@/components/CustomButton';
const { width, height } = Dimensions.get('window');
import { stories } from '@/constants';
import { router } from 'expo-router';



const HomeScreen = () => {


    const { width: screenWidth } = Dimensions.get('window');
const [selectedStory, setSelectedStory] = useState(null);
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [stores, setStores] = useState([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState(null);
  const [selectedSubCategoryId, setSelectedSubCategoryId] = useState(null);
  const [loading, setLoading] = useState(false);

  //   // states and refs for slider
  const [sliderImages, setSliderImages] = useState([]);
  const flatListRef = useRef(null);
  const currentIndex = useRef(0);


  const { currentUser: user, } = useSelector((state) => state?.auth);


  const API_BASE_URL = 'https://coffee.mahmoudalbatran.com/api';
  const AUTH_TOKEN = user?.data?.token; // ضع التوكن الخاص بك هنا

  const fetchCategories = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${API_BASE_URL}/category`, {
        headers: {
          Authorization: `Bearer ${AUTH_TOKEN}`,
          Accept: 'application/json',
        },
      });
      setCategories(response.data.categories);
    } catch (error) {
      console.error('Error fetching categories:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchSubCategories = async (categoryId) => {
    setLoading(true);
    try {
      setSelectedCategoryId(categoryId);
      setSelectedSubCategoryId(null);
      setStores([]);
      const response = await axios.get(`${API_BASE_URL}/sub-category/${categoryId}`, {
        headers: {
          Authorization: `Bearer ${AUTH_TOKEN}`,
          Accept: 'application/json',
        },
      });
      setSubCategories(response.data);
    } catch (error) {
      console.error('Error fetching subcategories:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStores = async (categoryId, subCategoryId) => {
    setLoading(true);
    try {
      setSelectedSubCategoryId(subCategoryId);
      const response = await axios.get(`${API_BASE_URL}/filter/${categoryId}/${subCategoryId}`, {
        headers: {
          Authorization: `Bearer ${AUTH_TOKEN}`,
          Accept: 'application/json',
        },
      });
      setStores(response.data.stores.data);
    } catch (error) {
      console.error('Error fetching stores:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

    useEffect(() => {
    const fetchSliderImages = async () => {
      try {
        const res = await axios.get('https://coffee.mahmoudalbatran.com/api/sliders',  {
          headers: { Authorization: `Bearer ${user?.data?.token}` },
        });
        const data = res?.data;
        console.log(data, 'rr')
        if (data?.sliders) {
          setSliderImages(data.sliders.map((item: any) => item.image));
        }
      } catch (error) {
        console.error('Failed to fetch slider images', error);
      }
    };
    fetchSliderImages();
  }, []);


  return (
    <ScrollView style={styles.container}>
      <View className="">
       <Text className="text-xl font-bold px-4 mb-2 text-black"> عروض اليوم</Text>
       <ScrollView horizontal showsHorizontalScrollIndicator={false} className="pl-4">
         {stories.map((story) => (
          <TouchableOpacity key={story.id} className="items-center mr-4 rounded-full" onPress={() => setSelectedStory(story)}>
            <LinearGradient
              colors={['#F59E0B', '#EF4444', '#8B5CF6']}
              // className="p-[2px] rounded-full "
              style={{
                padding: 2,
                borderRadius: 9999, // full rounding
              }}
            >
              <View className="w-16 h-16 rounded-full p-1 overflow-hidden">
              <Image
                source={{ uri: story.image }}
                className="w-full h-full rounded-full contain p-1 flex items-center justify-center"
                resizeMode="cover"
              />
            </View>

            </LinearGradient>
            
            <Text className="text-xs mt-1">{story.title}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

    </View>
    {/* Image Slider */}
         <View className='my-2'>
          {sliderImages.length > 0 && (
          <FlatList
            ref={flatListRef}
            data={sliderImages}
            horizontal
            pagingEnabled
            showsHorizontalScrollIndicator={false}
            keyExtractor={(item, index) => index.toString()}
            renderItem={({ item }) => (
              <Image
                source={{ uri: `https://coffee.mahmoudalbatran.com/${item}` }}
                style={{ width: screenWidth, height: 120, overflow: 'hidden' }}
                resizeMode="cover"
              />
            )}
          />
        )}
         </View>
      <Text style={styles.header}>التصنيفات</Text>
      {loading && <ActivityIndicator size="large" color="#000" />}
      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.scrollRow}>
        {categories.map((item) => (
          <TouchableOpacity key={item.id} onPress={() => fetchSubCategories(item.id)} style={styles.item} className={`${selectedCategoryId == item?.id ? 'bg-blue-100 p-2 rounded-lg' : 'p-2'}`}>
            <Image source={{ uri: `https://coffee.mahmoudalbatran.com/${item.image}` }} style={styles.image} />
            <Text style={styles.text}>{item.name}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      {subCategories.length > 0 && (
        <>
          <Text style={styles.header}>التصنيفات الفرعية</Text>
          <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.scrollRow}>
            {subCategories.map((item) => (
              <TouchableOpacity key={item.id} onPress={() => fetchStores(item.category_id, item.id)} style={styles.itemSmall}>
                <Text style={styles.text}>{item.name}</Text>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </>
      )}

      {stores.length > 0 && (
        <>
          <Text style={styles.header}>المتاجر</Text>
          <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.scrollRow}>
            {stores.map((item: any) => (
              <TouchableOpacity key={item.id} onPress={() => router.push(`/(root)/currentStore/${item.id}`)}>
                <View  style={styles.storeItem}>
                <Image source={{ uri: `https://coffee.mahmoudalbatran.com/${item.profile_photo_path}` }} style={styles.storeImage} />
                <Text style={styles.text}>{item.store_name}</Text>
                {/* <Text style={styles.text}>{item.description}</Text> */}
                <Text style={styles.text}>{item.phone_number}</Text>
              </View>
              </TouchableOpacity>
            ))}
          </ScrollView>
        </>
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 10 },
  header: { fontSize: 18, fontWeight: 'bold', marginVertical: 10 },
  scrollRow: { marginBottom: 10 },
  item: { marginRight: 10, alignItems: 'center' },
  itemSmall: { marginRight: 10, padding: 10, backgroundColor: '#eee', borderRadius: 10 },
  image: { width: 80, height: 80, borderRadius: 40 },
  text: { textAlign: 'center', marginTop: 5 },
  storeItem: { marginRight: 10, width: 150, backgroundColor: '#f5f5f5', borderRadius: 10, padding: 10 },
  storeImage: { width: '100%', height: 80, borderRadius: 10 },
});

export default HomeScreen;
