export default function HomeScreen() {

  const { currentUser: user, } = useSelector((state) => state?.auth);

  // states and refs for slider
  const [sliderImages, setSliderImages] = useState([]);
  const flatListRef = useRef(null);
  const currentIndex = useRef(0);
  // states for products view
  const [categories, setCategories] = useState([]);
  const [productsByCategory, setProductsByCategory] = useState<any>({});
  const [loading, setLoading] = useState(true);

  

  const { width: screenWidth } = Dimensions.get('window');

console.log(sliderImages, 'ffffffffffff')

  useEffect(() => {
    const fetchSliderImages = async () => {
      try {
        const res = await axios.get('https://coffee.mahmoudalbatran.com/api/sliders',  {
          headers: { Authorization: `Bearer ${user?.data?.token}` },
        });
        const data = res?.data;
        if (data?.sliders) {
          setSliderImages(data.sliders.map((item: any) => item.image));
        }
      } catch (error) {
        console.error('Failed to fetch slider images', error);
      }
    };
    fetchSliderImages();
  }, []);


  // Fetch categories from API
 useEffect(() => {
    const fetchCategoriesAndProducts = async () => {
      try {
        const categoryRes = await axios.get('https://coffee.mahmoudalbatran.com/api/category', {
          headers: { Authorization: `Bearer ${user?.data?.token}` },
        });
        const categoryData = categoryRes.data;

        if (categoryData.status && categoryData.categories) {
          setCategories(categoryData.categories);

          const allProducts: any = {};

          for (const category of categoryData.categories) {
            try {
              const productRes = await axios.get(`https://coffee.mahmoudalbatran.com/api/filter/${category.id}`, {
          headers: { Authorization: `Bearer ${user?.data?.token}` },
        });
              allProducts[category.id] = productRes.data.products?.data || [];
            } catch (productError) {
              console.warn(`Failed to fetch products for category ${category.id}`, productError);
              allProducts[category.id] = [];
            }
          }

          setProductsByCategory(allProducts);
        }
      } catch (error) {
        console.error('Error fetching categories:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCategoriesAndProducts();
  }, []);




    // Auto scroll effect
  useEffect(() => {
    const interval = setInterval(() => {
      if (sliderImages.length > 0) {
        currentIndex.current = (currentIndex.current + 1) % sliderImages.length;
        flatListRef.current?.scrollToIndex({ index: currentIndex.current, animated: true });
      }
    }, 3000); // every 3 seconds
    return () => clearInterval(interval);
  }, [sliderImages]);



  return (<SafeAreaView className="flex-1 bg-black">
      <StatusBar style="light" />
      <ScrollView className="">
        {/* Image Slider */}
        {sliderImages.length > 0 && (
          <FlatList
            ref={flatListRef}
            data={sliderImages}
            horizontal
            pagingEnabled
            showsHorizontalScrollIndicator={false}
            keyExtractor={(item, index) => index.toString()}
            renderItem={({ item }) => (
              <Image
                source={{ uri: `https://coffee.mahmoudalbatran.com/${item}` }}
                style={{ width: screenWidth, height: 120 }}
                resizeMode="contain"
              />
            )}
          />
        )}

        <View className="px-4">
          <Text className="text-white text-3xl font-bold mt-4 mb-4">مرحبا بك في كوفي الباقة ☕</Text>

          {/* {categories.map((category) => (
            <View key={category.id} className="mb-6">
              <Text className="text-white text-xl font-semibold mb-2">{category.title}</Text>
              <FlatList
                horizontal
                data={category.products}
                keyExtractor={(item) => item.id}
                showsHorizontalScrollIndicator={false}
                renderItem={({ item }) => (
                  <TouchableOpacity className="mr-4 w-40 bg-neutral-900 rounded-2xl overflow-hidden shadow-md">
                    <Image
                      source={{ uri: item.image }}
                      className="h-32 w-full"
                      resizeMode="cover"
                    />
                    <View className="p-3">
                      <Text className="text-white font-semibold text-base">{item.name}</Text>
                      <Text className="text-neutral-400 mt-1">{item.price}</Text>
                    </View>
                  </TouchableOpacity>
                )}
              />
            </View>
          ))} */}

          {loading ? (
          <ActivityIndicator size="large" color="#fff" />
        ) : (
          categories.map((category) => (
            <View key={category.id} className="mb-6">
              <Text className="text-white text-xl font-semibold mb-2">{category.name}</Text>
              <FlatList
                horizontal
                data={productsByCategory[category.id]}
                keyExtractor={(item) => item.id.toString()}
                showsHorizontalScrollIndicator={false}
                renderItem={({ item }) => (
                  <TouchableOpacity className="mr-4 w-40 bg-neutral-900 rounded-2xl overflow-hidden shadow-md">
                    <Image
                      source={{ uri: `https://coffee.mahmoudalbatran.com/${item.image}` }}
                      className="h-32 w-full"
                      resizeMode="cover"
                    />
                    <View className="p-3">
                      <Text className="text-white font-semibold text-base">{item.name}</Text>
                      <Text className="text-neutral-400 mt-1">{item.price}</Text>
                    </View>
                  </TouchableOpacity>
                )}
              />
            </View>
          ))
        )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}
